import React, {useState, useEffect}from 'react'
import './UI.css'

const UI = () => {

    const [mqSensor, setMqSensor] = useState("")
    useEffect(() => {
        fetch("https://api.thingspeak.com/channels/1448369/feeds.json?api_key=26YJJ2LHV1WQA3AL&results=1")
        .then(response => response.json())
        .then(data => setMqSensor(data.feeds[0]["field1"]))
    },[mqSensor])
    console.log(mqSensor)
    
    return(
        <div className="container">
                <div className="dashboard">
                <h1 className="dashboardTitle">Air Quality Sensors</h1>
                <img src="data:image/svg+xml;base64," sizes="10px"/>
                <h2 className="airValue">{mqSensor.substring(0, mqSensor.length - 2)} PPM</h2>
            </div>
        </div>
        
        
    )
}

export default UI
